import{Q as t}from"./QPage.8c2b46b4.js";import{_ as s}from"./FullscreenImage.326e9d0f.js";import{F as i,G as r,H as a,I as o,J as e}from"./index.649cc920.js";import"./render.ab0cb4b4.js";import"./QImg.fffa41ef.js";import"./QSpinner.60d1a70a.js";import"./vm.524c0073.js";import"./use-timeout.2a85f3fd.js";import"./use-prevent-scroll.5b219bee.js";import"./dom.c76da8aa.js";import"./use-tick.a127142b.js";const u=e("h4",null,"Pr\xE9sentation du chalet",-1),l=e("p",null," Notre chalet La Prise-Milord se trouve au nord du village de Saint-Sulpice, au Val-de-Travers, \xE0 une altitude de 860 m\xE8tres, \xE0 la lisi\xE8re d\u2019une belle for\xEAt. Situ\xE9 \xE0 l\u2019\xE9cart du village de Saint-Sulpice, notre chalet b\xE9n\xE9ficie d\u2019un tr\xE8s bon ensoleillement. Il est bien \xE9quip\xE9 pour les s\xE9jours et vacances d\u2019\xE9t\xE9 et d\u2019hiver. Le chalet convient particuli\xE8rement bien aux familles et aux \xE9coles pour les semaines vertes ou de neige. Bien que se trouvant en dehors de toute circulation routi\xE8re, elle est proche des transports publics (train \xE0 Fleurier et ligne de bus Fleurier \u2013 Les Verri\xE8res). Il est \xE9galement ouvert aux r\xE9unions de toutes sortes, d\u2019un week-end, d\u2019une journ\xE9e ou m\xEAme d\u2019une soir\xE9e. La location se fait sur r\xE9servation. Il compte 42 lits r\xE9partis dans des chambres de 1\xD72, 2\xD74, 4\xD76 et 1\xD78 lits (matelas mousse). Les sacs de couchage ou draps, ainsi que les pantoufles sont obligatoires; nous mettons \xE0 disposition les couvertures et les taies d\u2019oreillers. Un r\xE9fectoire (70 places) et une salle de jeux modulable en r\xE9fectoire (30 places) sont \xE0 disposition, de m\xEAme qu\u2019une cuisine parfaitement \xE9quip\xE9e; cuisini\xE8re \xE0 gaz (6 br\xFBleurs avec four), cuisini\xE8re \xE9lectrique, lave-vaisselle, eau courante chaude et froide, frigo et cong\xE9lateur. Acc\xE8s au WIFI, flipchart, Beamer (location Fr. 10.- par jour) \xE0 disposition. Cinq douches et 4 WC compl\xE8tent le confort de la maison. Celle-ci dispose d\u2019un chauffage central. Deux terrains de jeux ainsi qu\u2019une aire de pique-nique avec chemin\xE9e pour les grillades ont \xE9t\xE9 am\xE9nag\xE9s \xE0 l\u2019est. La place de jeux est s\xE9curis\xE9e. ",-1),I={__name:"DescriptionPage",setup(n){return(c,d)=>(i(),r(t,{class:"wrapper q-pa-md"},{default:a(()=>[u,o(s,{imagePath:"Chalet.jpg"}),l]),_:1}))}};export{I as default};
